;; gorilla-repl.fileformat = 1

;; **
;;; # Experiments with k-means
;;; 
;;; Experiments with k-means with nested ambiguous clusters.
;;; 
;;; The Plan
;;; --------
;;; 
;;; We need experiments for several initial clusterings from the worst to the best:
;;; 
;;; * All clusters except one in one corner
;;; * Move some objects to the second corner
;;; * Random clustering (or pseudo-random to repeat the experiment)
;;; * Mix objects between cluster in one corner
;;; 
;;; Also we need pictures:
;;; 
;;; * Initial clustering
;;; * Result of plain k-means
;;; * The best result of 2-component k-means
;;; 
;;; How to use the notebook
;;; -----------------------
;;; 
;;; Shift + enter evaluates code. Hit alt+g twice in quick succession or click the menu icon (upper-right corner) for more commands.
;; **

;; @@
(ns k-means.experiments
  (:require [gorilla-plot.core :as plot]
            [k-means.core :refer [clusterize!]]
            [k-means.data-transform :as tr]
            [k-means.clustering :refer [clusterize-iteration!]])
  (:use [incanter core io charts stats datasets]
        [incanter-gorilla.render]
        [gorilla-repl table html]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; Load coordinates of object and transform data to matrix of distances and matrix of proximities.
;; **

;; @@
(def data-path "/home/s/opensource/k_means_matlab/data/owsinski/nested_2lvls_ambiguous.csv")
(def data (read-dataset data-path :header false))
(def matrix-data (to-matrix data))
(def distances (tr/data->distances matrix-data))
(def proximities (tr/distances->proximities distances))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;k-means.experiments/proximities</span>","value":"#'k-means.experiments/proximities"}
;; <=

;; **
;;; Function to expand clustering from template.
;; **

;; @@
(defn template->clustering [template]
  (vec (flatten (map-indexed #(repeat %2 %1) template))))
