;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;k-means.experiments/template-&gt;clustering</span>","value":"#'k-means.experiments/template->clustering"}
;; <=

;; @@
; @example
;
(template->clustering [8 7 7 8 7 8 7 8])
