(defn draw-side-to-side [one two explanations]
  (table-view
    [[(draw-clustering one matrix-data) (draw-clustering two matrix-data)]]
    :columns (map html-view explanations)))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;k-means.experiments/draw-side-to-side</span>","value":"#'k-means.experiments/draw-side-to-side"}
;; <=

;; **
;;; Part 1. All clusters except one in one corner
;;; ---------------------------------------------
;; **

;; @@
(def initial-template [2 2 2 2 2 2 3 45])
(def initial-clustering (template->clustering initial-template))
(def plain-clustering (atom initial-clustering))
(def clustering (atom initial-clustering))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;k-means.experiments/clustering</span>","value":"#'k-means.experiments/clustering"}
;; <=

;; @@
(draw-clustering initial-clustering matrix-data)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/PNG;base64,\"/>","value":"#incanter_gorilla.render.ChartView{:content #<JFreeChart org.jfree.chart.JFreeChart@b8a201f>, :opts nil}"}
;; <=

;; **
;;; Result of plain k-means
;; **

;; @@
(defn clusterize
  ([weight clustering-atom]
   (clusterize
     weight
     @clustering-atom
     (clusterize-iteration! clustering-atom weight distances proximities)
     clustering-atom))
  ([weight old new clustering-atom]
   (when-not (= old new)
     (recur
       weight
       new
       (clusterize-iteration! clustering-atom weight distances proximities)
       clustering-atom))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;k-means.experiments/clusterize</span>","value":"#'k-means.experiments/clusterize"}
;; <=

;; @@
(clusterize 0 plain-clustering)
